mapper.sql:
SELECT
CLIENT_ID clientId,
CLIENT_NAME clientName,
CLIENT_PHONE clientPhone ,
CLIENT_EMAIL clientEmail,
CLIENT_ADDRESS clientAddress,
CLIENT_LEVEL clientLevel,
CLIENT_SEX clientSex,
CREATE_TIME  createTime,
MODIFY_TIME modifyTime,
creator
FROM
t_client
WHERE 1 = 1

    AND CLIENT_NAME = #{client.clientName}


    AND CLIENT_PHONE = #{client.clientPhone}


    AND CLIENT_EMAIL = #{client.clientEmail}


    AND CLIENT_ADDRESS = #{client.clientAddress}


    AND CLIENT_LEVEL = #{client.clientLevel}


    And CLIENT_SEX > #{client.clientSex}


    And CREATE_TIME < #{client.createTime}


    And CREATOR < #{client.creator}
