mapper.sql:
SELECT CLIENT_ID clientId, CLIENT_NAME clientName, CLIENT_PHONE clientPhone , CLIENT_EMAIL clientEmail, CLIENT_ADDRESS clientAddress, CLIENT_LEVEL clientLevel, CLIENT_SEX clientSex, CREATE_TIME createTime, MODIFY_TIME modifyTime, creator FROM t_client WHERE 1 = 1
AND CLIENT_NAME like CONCAT ('%',#{client.clientName},'%')
AND CLIENT_PHONE like CONCAT ('%',#{client.clientPhone},'%')
AND CLIENT_EMAIL like CONCAT ('%',#{client.clientEmail},'%')
AND CLIENT_ADDRESS like CONCAT ('%',#{client.clientAddress },'%')
AND CLIENT_LEVEL = #{client.clientLevel}
And CLIENT_SEX = #{client.clientSex}
And create_time > #{client.createTimeFrom}
And create_time < #{client.createTimeTo}
And CREATOR like CONCAT ('%',#{client.creator},'%')

mapper.update:
UPDATE t_client SET CLIENT_NAME=#{clientName} , CLIENT_PHONE=#{clientPhone},
CLIENT_EMAIL=#{clientEmail},  CLIENT_ADDRESS=#{clientAddress},
CLIENT_LEVEL=#{clientLevel}, CLIENT_SEX=#{clientSex},
CREATOR=#{creator}
WHERE CLIENT_ID=#{clientId}
